import React from "react";

function CreateArea(props) {
  return (
    <div>
      <form>
        <input name="title" placeholder="Title" onChange={props.handleChange} value={props.input.title} />
        <textarea name="content" placeholder="Take a note..." rows="3" onChange={props.handleChange} value={props.input.content} />
        <button onClick={props.addItem}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
