import React from "react";
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import Zoom from '@material-ui/core/Zoom';
function CreateArea(props) {
  return (
    <div>
      <form className="create-note">
        <input name="title" placeholder="Title" onChange={props.handleChange} value={props.input.title} />
        <textarea name="content" placeholder="Take a note..." rows={nRows} onChange={props.handleChange} value={props.input.content} onClick={handleClick}/>
        <Zoom in={true}>
        <Fab onClick={props.addItem}><AddIcon /></Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateArea;
